// /************* When user click on the individual item, item get delete away from the list *****************/
import React from "react"

function ToDoItem(props){
    return(
        <div onClick={props.onChecked()}>
            <li> 
            {props.text}
            </li>
        </div>) 
}

export default ToDoItem;

