  function decrease(){
    setCount(count -1);
  }

function increase(){
  //count++;
  setCount(count + 1);
  }  

return (
  <div className = "container">
    <h1>{count}</h1>
    <button onClick={increase}>+</button>
    <button onClick={decrease}>-</button>
  </div>
);

}

export default App;
