import React, { useState } from "react";

function App() {
  const [contact, setContact] = useState({
    fName: "",
    lName: "",
    email: ""
  });

  function handleChange(event){
    const {name, value} = event.target;

    setContact((prevValue) => {
      if(name === "fName"){
        return{}
